 <body class="mainbody">
	<script>
		
		function uploader(params)
		{
			var url = "/admin/api/upload";
			
			if(params.id)
			{
				url += '/id/' + params.id;
			}
			
			if(params.callback)
			{
				url += '/callback/' + params.callback;
			}
			
			if(params.type)
			{
				url += '/type/' + params.type;
			}
			
			// iframe层-父子操作
			layer.open({
				type: 2,
				title : '上传文件',
				area: ['500px', '500px'],
				fixed: false, // 不固定
				maxmin: false,
				content: url,
				end:function(){
					//alert(0); //有时间 看看怎么做
					//del();
				}
			});
		}
		
	</script>

 <body class="mainbody">
	<div class="container-fluid" style="padding:15px;">
		<div class="panel panel-danger">
			<div class="panel-heading">节点列表</div>
			<div class="panel-body">
				<div class="form-group">
					<div class="col-sm-10">
						<?php foreach($node as $value): ?>
						<div class="auth">
							<div class="title">
								<label><?php echo $value['title']; ?></label>
								[<a href="<?php echo url('node_edit', ['id' => $value['id']]); ?>">编辑</a>] [<a href="javascript:del(<?php echo $value['id']; ?>)">删除</a>] 
								<a type="button" class="btn btn-primary btn-xs" href="<?php echo url('node_add', ['pid' => $value['id']]); ?>">添加子节点</a>
							</div>
							<ul class="body">
								<?php foreach($value['child'] as $val): ?>
								<li>
									<?php echo $val['title']; ?> [<a href="<?php echo url('node_edit', ['id' => $val['id']]); ?>">编辑</a>] [<a href="javascript:del(<?php echo $val['id']; ?>)">删除</a>] 
								</li>
								<?php endforeach; ?>
							</ul>
						</div>
						<?php endforeach; ?>
					</div>
				</div>
			</div>
		</div>
	</div>
	<script type="text/javascript">
		function del(id)
		{
			if(confirm('确定要删除吗?'))
			{
				var url = "<?php echo url('admin/rbac/node_del', ['id' => 'str_replace']); ?>";
				
				window.location.href = url.replace('str_replace', id);
			}
		}
	</script>



