 <body class="mainbody">
	<script>
		
		function uploader(params)
		{
			var url = "/admin/api/upload";
			
			if(params.id)
			{
				url += '/id/' + params.id;
			}
			
			if(params.callback)
			{
				url += '/callback/' + params.callback;
			}
			
			if(params.type)
			{
				url += '/type/' + params.type;
			}
			
			// iframe层-父子操作
			layer.open({
				type: 2,
				title : '上传文件',
				area: ['500px', '500px'],
				fixed: false, // 不固定
				maxmin: false,
				content: url,
				end:function(){
					//alert(0); //有时间 看看怎么做
					//del();
				}
			});
		}
		
	</script>

	<div class="container-fluid" style="padding:15px;">
		<div class="panel panel-danger">
			<div class="panel-heading clearfix">
				<?php if(input('?spec_id')): ?>编辑<?php else: ?>添加<?php endif; ?>商品规格
				<a href="javascript:;" onclick="window.history.go(-1)" class="btn btn-default pull-right"><span class="glyphicon glyphicon-repeat"></span> 返回</a>
			</div>
			<div class="panel-body">
				<form class="form-horizontal" role="form" action="" method="post">
					<table class="table table-bordered">
						<tbody>
						<tr>
							<td>规格名称：</td>
							<td>
								<input type="text" name="spec_name" value="<?php echo (isset($specData['spec_name']) && ($specData['spec_name'] !== '')?$specData['spec_name']:''); ?>">             
							</td>
						</tr>  
						<tr>
							<td>所属商品类型：</td>
							<td>
								<select name="type_id" id="type_id">
									 <option value="">请选择</option>
									 <?php if(is_array($typeData) || $typeData instanceof \think\Collection || $typeData instanceof \think\Paginator): if( count($typeData)==0 ) : echo "" ;else: foreach($typeData as $key=>$value): ?>
									<option value="<?php echo $value['type_id']; ?>" <?php if(! empty($specData) && $specData['type_id'] == $value['type_id']): ?> selected='selected' <?php endif; ?> ><?php echo $value['type_name']; ?></option>
									<?php endforeach; endif; else: echo "" ;endif; ?> 
									</select>
								<span id="err_type_id" style="color:#F00; display:none;"></span>                                        
							</td>
						</tr> 
						<tr>
							<td>可选值列表：</td> 
							<td>
							<textarea rows="5" cols="30" name="spec_values"><?php echo (isset($specData['spec_items']) && ($specData['spec_items'] !== '')?$specData['spec_items']:''); ?></textarea>
							一行文字为一个选项值
							<span id="err_attr_values" style="color:#F00; display:none;"></span>
							</td>
						</tr>                                
						</tbody>                                
					</table>
					<div class="form-group">
						<div class="col-sm-12">
							<button type="submit" class="btn btn-default pull-right">保存</button>
						</div>
					</div>
				</form>
			</div>
		</div>
	</div>



