	<script>
		
		function uploader(params)
		{
			var url = "/admin/api/upload";
			
			if(params.id)
			{
				url += '/id/' + params.id;
			}
			
			if(params.callback)
			{
				url += '/callback/' + params.callback;
			}
			
			if(params.type)
			{
				url += '/type/' + params.type;
			}
			
			// iframe层-父子操作
			layer.open({
				type: 2,
				title : '上传文件',
				area: ['500px', '500px'],
				fixed: false, // 不固定
				maxmin: false,
				content: url,
				end:function(){
					//alert(0); //有时间 看看怎么做
					//del();
				}
			});
		}
		
	</script>
