 <body class="mainbody">
	<script>
		
		function uploader(params)
		{
			var url = "/admin/api/upload";
			
			if(params.id)
			{
				url += '/id/' + params.id;
			}
			
			if(params.callback)
			{
				url += '/callback/' + params.callback;
			}
			
			if(params.type)
			{
				url += '/type/' + params.type;
			}
			
			// iframe层-父子操作
			layer.open({
				type: 2,
				title : '上传文件',
				area: ['500px', '500px'],
				fixed: false, // 不固定
				maxmin: false,
				content: url,
				end:function(){
					//alert(0); //有时间 看看怎么做
					//del();
				}
			});
		}
		
	</script>

	<div class="container-fluid" style="padding:15px;">
		<div class="panel panel-danger">
			<div class="panel-heading">商品列表</div>
			<div class="panel-body">
				<form class="form-inline" method="post" action="<?php echo url('index'); ?>">
					<div class="form-group">
						<select class="form-control" name="category_id">
							<option value="0">请选择分类</option>
							<?php echo $cateInputHtml; ?>
						</select>
					</div>
					<div class="form-group">
						<select class="form-control" name="brand_id">
							<option value="0">请选择品牌</option>
							<?php if(is_array($brand) || $brand instanceof \think\Collection || $brand instanceof \think\Paginator): if( count($brand)==0 ) : echo "" ;else: foreach($brand as $key=>$brandValue): ?>
								<option value="<?php echo $brandValue['id']; ?>" <?php if(\think\Request::instance()->param('brand_id') == $brandValue['id']): ?>selected<?php endif; ?>><?php echo $brandValue['brand_name']; ?></option>
							<?php endforeach; endif; else: echo "" ;endif; ?>
						</select>
					</div>
					<div class="form-group">
						<input type="text" class="form-control" id="exampleInputEmail2" placeholder="请输入关键字" name="keywords" value="<?php echo (\think\Request::instance()->param('keywords') !== ''?\think\Request::instance()->param('keywords'):''); ?>">
					</div>
					<button type="submit" class="btn btn-default">搜索</button>
				</form>
			</div>
			<table class="table table-bordered table-hover">
				<tr class="danger">
					<td>id</td>
					<td>图片</td>
					<td>商品名称</td>
					<td>分类</td>
					<td>品牌</td>
					<td>市场价</td>
					<td>本店价格</td>
					<td>模型</td>
					<td>操作</td>
				</tr>
				<?php if(is_array($data) || $data instanceof \think\Collection || $data instanceof \think\Paginator): if( count($data)==0 ) : echo "" ;else: foreach($data as $key=>$value): ?>
				<tr>
					<td><?php echo $value['id']; ?></td>
					<td>
						<img src="/<?php echo $value['image']; ?>" width="100" height="100" />
					</td>
					<td><?php echo $value['goods_name']; ?></td>
					
					<!-- 也可以直接 获取  这样可以不用在模型那边创建一个获取函数-->
					<!-- <td><?php echo $value['cate']['cate_name']; ?></td> -->
					<td><?php echo $value['cate_name']; ?></td>
					<td><?php echo $value['brand_name']; ?></td>
					<td><?php echo $value['market_price']; ?></td>
					<td><?php echo $value['shop_price']; ?></td>
					<td><?php echo $value['type_name']; ?></td>
					<td>
						<a href="<?php echo url('admin/goods/edit', ['id' => $value['id']]); ?>" style="margin-right:10px;">
							<span class="glyphicon glyphicon-pencil"></span>
						</a>								
						<a href="javascript:;" onclick="del(<?php echo $value['id']; ?>)">
							<span class="glyphicon glyphicon-trash"></span>
						</a>
					</td>
				</tr>
				<?php endforeach; endif; else: echo "" ;endif; ?>
			</table>
			
			<!-- 分页码 -->
			<div class="page">
				<?php echo $data -> render(); ?>
			</div>
		</div>
	</div>
	<script type="text/javascript">
		function del(id)
		{
			if(confirm('确定要删除吗?'))
			{
				var url = "<?php echo url('admin/article/del', ['id' => 'str_replace']); ?>";
				
				window.location.href = url.replace('str_replace', id);
			}
		}
	</script>
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	
	

