									<label>
										<input type="checkbox" name="node[]" value="<?php echo $value['id']; ?>" /> <?php echo $value['title']; ?>
									</label>
								</div>
								<ul class="body">
									<?php foreach($value['child'] as $val): ?>
									<li>
										<label>
											<input type="checkbox" name="node[]" value="<?php echo $val['id']; ?>" /> <?php echo $val['title']; ?>
