 <body class="mainbody">
	<script>
		
		function uploader(params)
		{
			var url = "/admin/api/upload";
			
			if(params.id)
			{
				url += '/id/' + params.id;
			}
			
			if(params.callback)
			{
				url += '/callback/' + params.callback;
			}
			
			if(params.type)
			{
				url += '/type/' + params.type;
			}
			
			// iframe层-父子操作
			layer.open({
				type: 2,
				title : '上传文件',
				area: ['500px', '500px'],
				fixed: false, // 不固定
				maxmin: false,
				content: url,
				end:function(){
					//alert(0); //有时间 看看怎么做
					//del();
				}
			});
		}
		
	</script>

 <body class="mainbody">
	<div class="container-fluid" style="padding:15px;">
		<div class="panel panel-danger">
			<div class="panel-heading">角色列表</div>
			<div class="panel-body">
				<table class="table table-bordered table-hover">
					<tr class="danger">
						<td><input type="checkbox" name="id" value="option"></td>
						<td>ID</td>
						<td>角色名称</td>
						<td>用户名列表</td>
						<td>描述</td>
						<td>操作</td>
					</tr>
					<?php foreach($role as $key => $value): ?>
					<tr>
						<td><input type="checkbox" name="id" value="option<?php echo $key; ?>"></td>
						<td><?php echo $value['id']; ?></td>
						<td><?php echo $value['name']; ?></td>
						<td>阿卡丽</td>
						<td><?php echo $value['explain']; ?></td>
						<td>
