							<a class="glyphicon glyphicon-trash" href="javascript:del(<?php echo $value['id']; ?>)" ></a>
						</td>
					</tr>
					<?php endforeach; ?>
				</table>
			</div>
		</div>
	</div>
	<script type="text/javascript">
		function del(id)
		{
			if(confirm('确定要删除吗?'))
			{
				var url = "<?php echo url('admin/rbac/role_del', ['id' => 'str_replace']); ?>";
				
				window.location.href = url.replace('str_replace', id);
			}
		}
	</script>



