 <body class="mainbody">
	<script>
		
		function uploader(params)
		{
			var url = "/admin/api/upload";
			
			if(params.id)
			{
				url += '/id/' + params.id;
			}
			
			if(params.callback)
			{
				url += '/callback/' + params.callback;
			}
			
			if(params.type)
			{
				url += '/type/' + params.type;
			}
			
			// iframe层-父子操作
			layer.open({
				type: 2,
				title : '上传文件',
				area: ['500px', '500px'],
				fixed: false, // 不固定
				maxmin: false,
				content: url,
				end:function(){
					//alert(0); //有时间 看看怎么做
					//del();
				}
			});
		}
		
	</script>

 <body class="mainbody">
	<div class="container-fluid" style="padding:15px;">
		<div class="panel panel-danger">
			<div class="panel-heading">商品模型列表</div>
			<div class="panel-body">
				<table class="table table-bordered table-hover">
					<tr class="danger">
						<td>id</td>
						<td>模型名称</td>
						<td>操作</td>
					</tr>
					<?php if(is_array($cateData) || $cateData instanceof \think\Collection || $cateData instanceof \think\Paginator): if( count($cateData)==0 ) : echo "" ;else: foreach($cateData as $key=>$value): ?>
					<tr>
						<td><?php echo $value['type_id']; ?></td>
						<td><?php echo $value['type_name']; ?></td>
						<td>
							<a href="<?php echo url('admin/goods_attr/index', ['type_id' => $value['type_id']]); ?>" data-toggle="tooltip" title="" class="btn btn-info" data-original-title="属性列表">属性列表</a>
							<a href="<?php echo url('admin/goods_spec/index', ['type_id' => $value['type_id']]); ?>" data-toggle="tooltip" title="" class="btn btn-info" data-original-title="属性列表">规格列表</a>
							<a href="<?php echo url('admin/goods_attr/edit', ['type_id' => $value['type_id']]); ?>" style="margin-right:10px;">
								<span class="glyphicon glyphicon-pencil"></span>
							</a>								
							<a href="javascript:;" onclick="del(<?php echo $value['type_id']; ?>)">
								<span class="glyphicon glyphicon-trash"></span>
							</a>
						</td>
					</tr>
					<?php endforeach; endif; else: echo "" ;endif; ?>
				</table>
			</div>
		</div>
	</div>
	<script type="text/javascript">
		function del(id)
		{
			if(confirm('确定要删除吗?'))
			{
				var url = "<?php echo url('admin/goods_type/del', ['type_id' => 'str_replace']); ?>";
				
				window.location.href = url.replace('str_replace', id);
			}
		}
	</script>



