 <body class="mainbody">
	<script>
		
		function uploader(params)
		{
			var url = "/admin/api/upload";
			
			if(params.id)
			{
				url += '/id/' + params.id;
			}
			
			if(params.callback)
			{
				url += '/callback/' + params.callback;
			}
			
			if(params.type)
			{
				url += '/type/' + params.type;
			}
			
			// iframe层-父子操作
			layer.open({
				type: 2,
				title : '上传文件',
				area: ['500px', '500px'],
				fixed: false, // 不固定
				maxmin: false,
				content: url,
				end:function(){
					//alert(0); //有时间 看看怎么做
					//del();
				}
			});
		}
		
	</script>

 <body class="mainbody">
	<div class="container-fluid" style="padding:15px;">
		<div class="panel panel-danger">
			<div class="panel-heading">管理员列表</div>
			<div class="panel-body">
				<table class="table table-bordered table-hover">
					<tr class="danger">
						<td>ID</td>
						<td>管理员名称</td>
						<td>状态</td>
						<td>操作</td>
					</tr>
					<?php foreach($admin as $value): ?>
					<tr>
						<td><?php echo $value['id']; ?></td>
						<td><?php echo $value['username']; ?></td>
						<td>
							<?php if($value['status'] == 1): ?>
							<a href="<?php echo url('admin_status', ['id' => $value['id'], 'status' => 2]); ?>" class="btn btn-success btn-sm">开启</a>
							<?php else: ?>
							<a href="<?php echo url('admin_status', ['id' => $value['id'], 'status' => 1]); ?>" class="btn btn-danger btn-sm">关闭</a>
							<?php endif; ?>
						</td>
						<td>
							<a href="<?php echo url('admin_edit', ['id' => $value['id']]); ?>"><span class="glyphicon glyphicon-pencil"></span></a>　
							<a href="javascript:del(<?php echo $value['id']; ?>);"><span class="glyphicon glyphicon-trash"></span></a>
						</td>
					</tr>
					<?php endforeach; ?>
				</table>
			</div>
		</div>
	</div>
	<script type="text/javascript">
		function del(id)
		{
			if(confirm('确定要删除吗?'))
			{
				var url = "<?php echo url('admin/rbac/admin_del', ['id' => 'str_replace']); ?>";
				
				window.location.href = url.replace('str_replace', id);
			}
		}
	</script>



